import numpy as np
import scipy
import matplotlib.pyplot as plt



def smooth_prices(stock_prices: list[float], lam: float=20, window: int=20, degree: int=4):
    stock_prices_indexes = list(range(len(stock_prices)))
    splinify_stock_prices = scipy.interpolate.make_smoothing_spline(stock_prices_indexes, stock_prices, lam=lam)
    plt.plot(stock_prices_indexes, splinify_stock_prices(stock_prices_indexes), "-g")
    plt.plot(stock_prices_indexes, list(scipy.signal.savgol_filter(stock_prices, window, degree)), "-c")
    plt.plot(stock_prices_indexes, stock_prices, 'oy')
    plt.show()
    return list(splinify_stock_prices(stock_prices_indexes))


